		              ,prodCnt
		              ,delSts
		              ,delDsts
		            ) VALUES (
		                        #{delNo}
		                       ,${coNo}
		                       ,${strNo}
		                       ,${prodNo}
		                       ,${prodCnt}
		                       ,#{delSts}
		                       ,${delDsts}
		                     )
