mapper.select:
select tp.prodNo ,tp.prodName ,td.detail_name as prodCate ,tp.mfName ,tp.selPrice from tb_product tp inner join ( select detail_code ,detail_name from tb_detail_code ) td on tp.prodCate = td.detail_code
and tp.prodCate = #{searchCt} and tp.prodName LIKE '%${searchPn}%'
order by tp.prodNo desc LIMIT #{pageStartN}, #{pageRow}

select count(*)
from tb_cart

	and loginID=#{loginID}
	and prodNo=#{prodNo}

mapper.insert:

select ifnull(max(cartNo),0)+1 from tb_cart

insert into tb_cart
	   (
       cartNo
	   ,prodNo
       ,loginID
       ,cartCnt
       ,cartDel
       )
       values(
       #{counting_cartNo}
       ,#{prodNo}
       ,#{loginID}
       ,#{cartCnt}
       ,#{cartDel}
       )


select ifnull(max(coNo),0)+1 from tb_cOrder

insert into tb_cOrder
	   (
    		coNo
    		,prodNo
    		,loginID
    		,coCnt
    		,coDate
    		,coDel
    		,coSts
    		,coDpsts
       )
       values(
        #{counting_orderNo}
       ,#{prodNo}
       ,#{loginID}
       ,#{coCnt}
       ,#{coDate}
       ,#{coDel}
       ,#{coSts}
       ,#{coDpsts}

       )
